Start-Process -WindowStyle Hidden powershell.exe -ArgumentList '-NoProfile -ExecutionPolicy Bypass -Command "$src=''C:\Users\kusha\Downloads''; $dest=Join-Path $src ''processed''; if(!(Test-Path $dest)){New-Item -ItemType Directory -Path $dest|Out-Null}; while($true){Get-ChildItem (Join-Path $src ''*.toml'') -ErrorAction SilentlyContinue | ForEach-Object {Move-Item $_.FullName -Destination $dest -Force}; $count=(Get-ChildItem $dest -Filter *.toml -ErrorAction SilentlyContinue | Measure-Object).Count; ''Total processed files: ''+$count | Out-File -FilePath (Join-Path $dest ''processed_count.log'') -Append; Start-Sleep 10}"'
