Start-Process -WindowStyle Hidden powershell.exe -ArgumentList '-NoProfile -ExecutionPolicy Bypass -Command "$src=Join-Path $env:USERPROFILE ''Downloads''; $dest=Join-Path $src ''processed''; if(!(Test-Path $dest)){New-Item -ItemType Directory -Path $dest|Out-Null}; $script=''C:\Scripts\run_toml.py''; $py=''python3''; $outLog=Join-Path $dest ''run_toml_output.log''; $countLog=Join-Path $dest ''processed_count.log''; while($true){ Get-ChildItem (Join-Path $src ''*.toml'') -ErrorAction SilentlyContinue | ForEach-Object { $f=$_.FullName; & $py $script $f *>> $outLog; Move-Item $f -Destination $dest -Force }; $count=(Get-ChildItem $dest -Filter *.toml -ErrorAction SilentlyContinue | Measure-Object).Count; (''Total processed files: ''+$count) | Out-File -FilePath $countLog -Append; Start-Sleep 10 }"'
